import * as React from "react";
import PropTypes from "prop-types";
import Box from "@mui/material/Box";
import CssBaseline from "@mui/material/CssBaseline";
import Divider from "@mui/material/Divider";
import Drawer from "@mui/material/Drawer";
import IconButton from "@mui/material/IconButton";
import LinkedInIcon from "@mui/icons-material/LinkedIn";
import GitHubIcon from "@mui/icons-material/GitHub";
import AlternateEmailIcon from "@mui/icons-material/AlternateEmail";
import PersonIcon from "@mui/icons-material/Person";
import ComputerIcon from "@mui/icons-material/Computer";
import DescriptionIcon from "@mui/icons-material/Description";
import ContactMailIcon from "@mui/icons-material/ContactMail";
import SwitchButton from "./SwitchButton";
import { Link } from "react-router-dom";

const drawerWidth = 240;

function ResponsiveDrawer(props) {
  const { window } = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <div style={{ paddingRight: "5%", paddingLeft: "5%" }}>
      <div className="mt-3" style={{ textAlign: "center", color: "white" }}>
        <b style={{ fontSize: "150%" }}> Omer Perez</b>
      </div>
      <div className="mt-3 d-flex justify-content-center">
        <img
          style={{
            width: "170px",
            height: "170px",
            borderRadius: "50%",
          }}
          src="../Images/Profile Image.png"
        />
      </div>
      <div className="mt-3" style={{ textAlign: "center" }}>
        <p style={{ color: "white", fontSize: "95%" }}>
          Hi, my name is Omer Perez and I'm a junior Full Stack developer.
          Welcome to my personal website!
        </p>
        <div className="d-flex justify-content-center">
          <div className="row" style={{ minWidth: "100px" }}>
            <div className="col-2 offset-1">
              <IconButton
                aria-label="delete"
                size="large"
                style={{ color: "white" }}
              >
                <LinkedInIcon
                  fontSize="large"
                  style={{
                    borderRadius: "50%",
                    backgroundColor: "#54B689",
                    color: "white",
                    border: "2px solid white",
                  }}
                />
              </IconButton>
            </div>
            <div className="col-2 offset-1">
              <IconButton
                aria-label="delete"
                size="large"
                style={{ color: "white" }}
              >
                <GitHubIcon
                  fontSize="large"
                  style={{
                    borderRadius: "50%",
                    color: "#54B689",
                    backgroundColor: "white",
                  }}
                />
              </IconButton>
            </div>
            <div className="col-2 offset-1">
              <IconButton
                aria-label="delete"
                size="large"
                style={{ color: "white" }}
              >
                <AlternateEmailIcon
                  fontSize="large"
                  style={{
                    borderRadius: "50%",
                    color: "#54B689",
                    backgroundColor: "white",
                  }}
                />
              </IconButton>
            </div>
          </div>
        </div>
      </div>
      <Divider className="mt-3 mb-3" style={{ color: "white" }} />
      <Link
        to="/AboutMe"
        style={{ textDecoration: "none" }}
        className="mt-2 d-flex justify-content-start offset-3"
      >
        <IconButton aria-label="delete" size="large" style={{ color: "white" }}>
          <PersonIcon
            fontSize="medium"
            style={{
              borderRadius: "50%",
              color: "#cccccc",
              marginRight: "7px",
            }}
          />
          <b style={{ fontSize: "65%" }}>About Me</b>
        </IconButton>
      </Link>
      <Link
        to="/Projects"
        style={{ textDecoration: "none" }}
        className="mt-2 d-flex justify-content-start offset-3"
      >
        <IconButton aria-label="delete" size="large" style={{ color: "white" }}>
          <ComputerIcon
            fontSize="medium"
            style={{
              borderRadius: "50%",
              color: "#cccccc",
              marginRight: "7px",
            }}
          />
          <b style={{ fontSize: "65%" }}>Projects</b>
        </IconButton>
      </Link>
      <Link
        to="/Resume"
        className="mt-2 d-flex justify-content-start offset-3"
        style={{ textDecoration: "none" }}
      >
        <IconButton aria-label="delete" size="large" style={{ color: "white" }}>
          <DescriptionIcon
            fontSize="medium"
            style={{
              borderRadius: "50%",
              color: "#cccccc",
              marginRight: "7px",
            }}
          />
          <b style={{ fontSize: "65%" }}>Resume</b>
        </IconButton>
      </Link>
      <div className="mt-2 d-flex justify-content-start offset-3">
        <IconButton aria-label="delete" size="large" style={{ color: "white" }}>
          <ContactMailIcon
            fontSize="medium"
            style={{
              borderRadius: "50%",
              color: "#cccccc",
              marginRight: "7px",
            }}
          />
          <b style={{ fontSize: "65%" }}>Contact</b>
        </IconButton>
      </div>
      <Divider className="mt-3 mb-3" style={{ color: "white" }} />
      <div className="d-flex justify-content-center mt-4">
        <SwitchButton />
      </div>
    </div>
  );

  const container =
    window !== undefined ? () => window().document.body : undefined;

  return (
    <div>
      <Box sx={{ display: "flex" }}>
        <CssBaseline />
        <Box
          component="nav"
          sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
          aria-label="mailbox folders"
        >
          <Drawer
            variant="permanent"
            sx={{
              display: { xs: "none", sm: "block" },
              "& .MuiDrawer-paper": {
                boxSizing: "border-box",
                width: "15%",
                backgroundColor: "#1e2a3a",
                borderRight: "3px solid #54B689",
              },
            }}
            open
          >
            {drawer}
          </Drawer>
        </Box>
      </Box>
    </div>
  );
}

ResponsiveDrawer.propTypes = {
  window: PropTypes.func,
};

export default ResponsiveDrawer;
