import * as React from "react";
import PropTypes from "prop-types";
import Box from "@mui/material/Box";
import CssBaseline from "@mui/material/CssBaseline";
import Divider from "@mui/material/Divider";
import Drawer from "@mui/material/Drawer";
import IconButton from "@mui/material/IconButton";
import InboxIcon from "@mui/icons-material/MoveToInbox";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import MailIcon from "@mui/icons-material/Mail";
import Toolbar from "@mui/material/Toolbar";
import LinkedInIcon from "@mui/icons-material/LinkedIn";
import GitHubIcon from "@mui/icons-material/GitHub";

const drawerWidth = 240;

function ResponsiveDrawer(props) {
  const { window } = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <div style={{ backgroundColor: "#333333" }}>
      <Toolbar />
      {/* <Divider /> */}
      <List>
        {["Inbox", "Starred", "Send email", "Drafts"].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>
              {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        {["All mail", "Trash", "Spam"].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>
              {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
    </div>
  );

  const container =
    window !== undefined ? () => window().document.body : undefined;

  return (
    <div>
      <Box sx={{ display: "flex" }}>
        <CssBaseline />
        <Box
          component="nav"
          sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
          aria-label="mailbox folders"
        >
          {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
          <Drawer
            container={container}
            variant="temporary"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
            sx={{
              display: { xs: "block", sm: "none" },
              "& .MuiDrawer-paper": {
                boxSizing: "border-box",
                width: drawerWidth,
              },
            }}
          >
            {drawer}
          </Drawer>
          <Drawer
            variant="permanent"
            sx={{
              display: { xs: "none", sm: "block" },
              "& .MuiDrawer-paper": {
                boxSizing: "border-box",
                width: "15%",
                backgroundColor: "#333333",
                borderRight: "3px solid #00b386",
              },
            }}
            open
          >
            <div style={{ paddingRight: "5%", paddingLeft: "5%" }}>
              <div
                className="mt-3"
                style={{ textAlign: "center", color: "white" }}
              >
                <b style={{ fontSize: "150%" }}> Omer Perez</b>
              </div>
              <div className="mt-4 d-flex justify-content-center">
                <img
                  style={{
                    width: "150px",
                    height: "150px",
                    borderRadius: "50%",
                  }}
                  src="../Images/Profile Image.png"
                />
              </div>
              <div className="mt-3" style={{ textAlign: "center" }}>
                <p style={{ color: "white", fontSize: "95%" }}>
                  Hi, my name is Omer Perez and I'm a junior Full Stack
                  developer. Welcome to my personal website!
                </p>
              </div>
              <div className="d-flex justify-content-center">
                <div className="row">
                  <div className="col">
                    <IconButton
                      aria-label="delete"
                      size="large"
                      style={{ color: "white" }}
                    >
                      <LinkedInIcon
                        fontSize="large"
                        style={{
                          // width: "20%",
                          // height: "20%",
                          borderRadius: "50%",
                          backgroundColor: "#00b386",
                          color: "white",
                          border: "2px solid white",
                        }}
                      />
                      {/* <img
                      style={{
                        // width: "50%",
                        // height: "50%",
                        borderRadius: "50%",
                        // backgroundColor: "#00b386",
                      }}
                      src="../Images/LinkdinIcon.png"
                    /> */}
                    </IconButton>
                  </div>
                  <div className="col">
                    <IconButton
                      aria-label="delete"
                      size="large"
                      style={{ color: "white" }}
                    >
                      <GitHubIcon
                        fontSize="large"
                        style={{
                          // width: "20%",
                          // height: "20%",
                          borderRadius: "50%",
                          color: "#00b386",
                          backgroundColor: "white",
                          // border: "1px solid white",
                        }}
                      />
                    </IconButton>
                  </div>
                </div>
              </div>
            </div>
            {/* {drawer} */}
          </Drawer>
        </Box>
      </Box>
    </div>
  );
}

ResponsiveDrawer.propTypes = {
  window: PropTypes.func,
};

export default ResponsiveDrawer;
